import React, { useState, useEffect } from "react";
import DataDisplay from "./DataDisplay";

const WebSocketComponent = () => {
    const [socket, setSocket] = useState(null);
    const [ipAddress, setIpAddress] = useState("");
    const [data, setData] = useState([]);
    const [status, setStatus] = useState("Disconnected");

    useEffect(() => {
        // Kullanıcı giriş yapmış mı kontrol et
        const token = localStorage.getItem("token");
        if (!token) {
            alert("Unauthorized. Please log in.");
            <Route path="/login" element={<Login />} />// Login ekranına yönlendir
            return;
        }

        // WebSocket bağlantısı oluştur
        const ws = new WebSocket("ws://localhost:5000/ws");
        setSocket(ws);

        ws.onopen = () => {
            console.log("WebSocket bağlantısı kuruldu");
            setStatus("Connected");
        };

        ws.onmessage = (event) => {
            setData((prevData) => [...prevData, event.data]);
        };

        ws.onclose = () => {
            console.log("WebSocket bağlantısı kapandı");
            setStatus("Disconnected");
        };

        return () => ws.close();
    }, []);

    const startCommunication = () => {
        if (!ipAddress) {
            alert("Please enter a valid IP address.");
            return;
        }
        if (socket && socket.readyState === WebSocket.OPEN) {
            const command = {
                action: "startCommunication",
                parameters: {
                    ipAddress: ipAddress,
                },
            };
            socket.send(JSON.stringify(command));
        } else {
            alert("WebSocket bağlantısı henüz kurulmadı.");
        }
    };

    const stopCommunication = () => {
        if (socket) {
            const command = { action: "stopCommunication" };
            socket.send(JSON.stringify(command));
        }
    };

    return (
        <div>
            <h2>SNMP Communication</h2>
            <p>Status: {status}</p>
            <div>
                <label>IP Address:</label>
                <input
                    type="text"
                    value={ipAddress}
                    onChange={(e) => setIpAddress(e.target.value)}
                    placeholder="Enter IP address"
                />
            </div>
            <button onClick={startCommunication}>Start Communication</button>
            <button onClick={stopCommunication}>Stop Communication</button>
            <DataDisplay data={data} />
        </div>
    );
};

export default WebSocketComponent;
