import React, { useState, useEffect } from 'react';
import DataDisplay from './DataDisplay';

const WebSocketComponent = () => {
    const [socket, setSocket] = useState(null);
    const [ipAddress, setIpAddress] = useState(''); // IP adresi girişi
    const [data, setData] = useState([]); // Gelen veriler

    useEffect(() => {
        const ws = new WebSocket('ws://localhost:5003/ws'); // WebSocket bağlantısı
        setSocket(ws);

        ws.onopen = () => {
            console.log("WebSocket bağlantısı kuruldu");
        };

        ws.onmessage = (event) => {
            setData(prevData => [...prevData, event.data]); // Yeni veriyi ekle
        };

        ws.onclose = () => {
            console.log("WebSocket bağlantısı kapandı");
        };

        return () => ws.close();
    }, []);

    const startCommunication = () => {
        if (socket && ipAddress) {
            const command = {
                action: "startCommunication",
                parameters: {
                    ipAddress: ipAddress
                }
            };
            socket.send(JSON.stringify(command));
        }
    };

    const stopCommunication = () => {
        if (socket) {
            const command = { action: "stopCommunication" };
            socket.send(JSON.stringify(command));
        }
    };

    return (
        <div>
            <h2>SNMP İletişimi</h2>
            <div>
                <label>IP Adresi:</label>
                <input
                    type="text"
                    value={ipAddress}
                    onChange={(e) => setIpAddress(e.target.value)}
                    placeholder="10.0.20.69"
                />
            </div>
            <button onClick={startCommunication}>İletişimi Başlat</button>
            <button onClick={stopCommunication}>İletişimi Durdur</button>

            <DataDisplay data={data} /> {/* Veriyi gösteren bileşen */}
        </div>
    );
};

export default WebSocketComponent;
