import React, { useEffect, useState } from "react";
import "./login.css";

const Login = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const token = localStorage.getItem("token");

    const handleLogin = async () => {
        try {
            const response = await fetch("http://localhost:5000/api/Login/authenticate", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ username, password }),
            });

            if (response.ok) {
                const data = await response.json();
                localStorage.setItem("token", data.token); // Token'ı kaydet
                window.location.href = "/snmp"; // SNMP ekranına yönlendir
            } else {
                setError("Invalid username or password.");
            }
        } catch (err) {
            console.error("Login Error:", err);
            setError("An error occurred while logging in. Please try again.");
        }
    };

    useEffect(() => {
        // Kullanıcı giriş yapmış mı kontrol et
        if (token) {
            window.location.href = "/snmp"; // Kullanıcı zaten giriş yaptıysa SNMP ekranına yönlendir
        }
    }, [token]);

    return (
        <div className="login-container">
            <h2>Login</h2>
            {error && <p className="error-message">{error}</p>}
            <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="login-input"
            />
            <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="login-input"
            />
            <button onClick={handleLogin} className="login-button">
                Login
            </button>
        </div>
    );
};

export default Login;
