import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./Login";
import WebSocketComponent from "./WebSocketComponent";
import DeviceAdd from "./DeviceAdd"; // Cihaz ekleme ekranı

const App = () => {
    const isLoggedIn = !!localStorage.getItem("token"); // Eğer token varsa giriş yapılmış kabul edilir

    return (
        <Router>
            <div style={{ display: "flex", height: "100vh" }}>
                {/* Sol Menü */}
                {isLoggedIn && (
                    <nav
                        style={{
                            width: "200px",
                            background: "#f4f4f4",
                            padding: "10px",
                            boxShadow: "2px 0 5px rgba(0, 0, 0, 0.1)",
                        }}
                    >
                        <ul style={{ listStyle: "none", padding: 0 }}>
                            <li style={{ marginBottom: "10px" }}>
                                <a href="/snmp" style={{ textDecoration: "none", color: "#333" }}>
                                    SNMP Communication
                                </a>
                            </li>
                            <li>
                                <a href="/add-device" style={{ textDecoration: "none", color: "#333" }}>
                                    Add Device
                                </a>
                            </li>
                        </ul>
                    </nav>
                )}

                {/* Sayfa İçeriği */}
                <div style={{ flex: 1, padding: "20px" }}>
                    <Routes>
                        {/* Login Sayfası */}
                        <Route path="/login" element={<Login />} />

                        {/* SNMP Communication Sayfası */}
                        <Route
                            path="/snmp"
                            element={
                                isLoggedIn ? (
                                    <WebSocketComponent />
                                ) : (
                                    <Navigate to="/login" />
                                )
                            }
                        />

                        {/* Cihaz Ekleme Sayfası */}
                        <Route
                            path="/add-device"
                            element={
                                isLoggedIn ? <DeviceAdd /> : <Navigate to="/login" />
                            }
                        />

                        {/* Varsayılan Yönlendirme */}
                        <Route
                            path="/"
                            element={<Navigate to={isLoggedIn ? "/snmp" : "/login"} />}
                        />
                    </Routes>
                </div>
            </div>
        </Router>
    );
};

export default App;
