import React, { useState, useEffect } from 'react';
import DataDisplay from './DataDisplay';

const WebSocketComponent = () => {
  const [socket, setSocket] = useState(null);
  const [ipAddress, setIpAddress] = useState(''); // Kullanıcıdan IP adresi alıyoruz
  const [data, setData] = useState([]);

  useEffect(() => {
    if (!ipAddress) return; // IP adresi olmadan WebSocket başlatılmaz

    const ws = new WebSocket(`ws://${ipAddress}:5005/ws`); // 5005 portu
    setSocket(ws);

    ws.onopen = () => {
      console.log("SNMP cihazına WebSocket bağlantısı kuruldu");
    };

    ws.onmessage = (event) => {
      console.log("Mesaj alındı:", event.data);
      setData((prevData) => [...prevData, event.data]);
    };

    ws.onclose = () => {
      console.log("WebSocket bağlantısı kapandı");
    };

    ws.onerror = (error) => {
      console.error("WebSocket hatası:", error);
    };

    return () => ws.close(); // Bileşen unmount olduğunda bağlantıyı kapat
  }, [ipAddress]);

  const startCommunication = () => {
    if (socket) {
      const command = {
        action: 'startCommunication',
        parameters: {
          ipAddress: ipAddress,
        },
      };
      socket.send(JSON.stringify(command));
    }
  };

  const stopCommunication = () => {
    if (socket) {
      const command = { action: 'stopCommunication' };
      socket.send(JSON.stringify(command));
    }
  };

  return (
    <div>
      <h2>SNMP Communication</h2>
      <div>
        <label>IP Address:</label>
        <input
          type="text"
          value={ipAddress}
          onChange={(e) => setIpAddress(e.target.value)}
          placeholder="Enter IP address"
        />
      </div>
      <button onClick={startCommunication} disabled={!ipAddress}>
        Start Communication
      </button>
      <button onClick={stopCommunication} disabled={!socket}>
        Stop Communication
      </button>
      <DataDisplay data={data} />
    </div>
  );
};

export default WebSocketComponent;
