import React, { useState, useEffect } from 'react';
import DataDisplay from './DataDisplay';

const WebSocketComponent = () => {
    const [socket, setSocket] = useState(null);
    const [data, setData] = useState([]);
    const snmpDeviceIP = '10.0.20.69'; // SNMP cihazının IP adresi
    const snmpDevicePort = 5005; // SNMP cihazının portu

    useEffect(() => {
        const ws = new WebSocket(`ws://${snmpDeviceIP}:${snmpDevicePort}/ws`);
        setSocket(ws);

        ws.onopen = () => {
            console.log("SNMP cihazına bağlantı kuruldu.");
        };

        ws.onmessage = (event) => {
            setData((prevData) => [...prevData, event.data]);
            console.log("Mesaj alındı:", event.data);
        };

        ws.onclose = () => {
            console.log("SNMP cihazıyla bağlantı kapandı.");
        };

        return () => ws.close();
    }, []);

    const startCommunication = () => {
        if (socket) {
            const command = {
                action: 'startCommunication',
            };
            socket.send(JSON.stringify(command));
        }
    };

    const stopCommunication = () => {
        if (socket) {
            const command = { action: 'stopCommunication' };
            socket.send(JSON.stringify(command));
        }
    };

    return (
        <div>
            <h2>SNMP İletişimi</h2>
            <button onClick={startCommunication}>İletişimi Başlat</button>
            <button onClick={stopCommunication}>İletişimi Durdur</button>
            <DataDisplay data={data} />
        </div>
    );
};

export default WebSocketComponent;
