import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./Login";
import SNMP from "./"
import DeviceAdd from "./DeviceAdd"; // Yeni cihaz ekleme ekranı

const App = () => {
    const isLoggedIn = !!localStorage.getItem("token"); // Giriş kontrolü

    return (
        <Router>
            <div style={{ display: "flex", height: "100vh" }}>
                {/* Sol Menü */}
                {isLoggedIn && (
                    <nav
                        style={{
                            width: "200px",
                            background: "#f4f4f4",
                            padding: "10px",
                            boxShadow: "2px 0 5px rgba(0, 0, 0, 0.1)",
                            position: "fixed",
                            height: "100%",
                        }}
                    >
                        <ul style={{ listStyle: "none", padding: 0, margin: 0 }}>
                            <li>
                                <a href="/add-device" style={{ textDecoration: "none", color: "#333" }}>
                                    Add Device
                                </a>
                            </li>
                        </ul>
                    </nav>
                )}

                {/* Sayfa İçeriği */}
                <div style={{ flex: 1, padding: "20px", marginLeft: "200px" }}>
                    <Routes>
                        {/* Login Sayfası */}
                        <Route path="/login" element={<Login />} />

                        {/* Cihaz Ekleme Sayfası */}
                        <Route
                            path="/add-device"
                            element={
                                isLoggedIn ? <DeviceAdd /> : <Navigate to="/login" />
                            }
                        />

                        {/* Varsayılan Yönlendirme */}
                        <Route
                            path="/"
                            element={<Navigate to={isLoggedIn ? "/add-device" : "/login"} />}
                        />
                    </Routes>
                </div>
            </div>
        </Router>
    );
};

export default App;
